import React, { useEffect, useState } from "react";
import { Container, Typography, Paper, Grid } from "@mui/material";

const Dashboard = () => {
  const [totalRevenue, setTotalRevenue] = useState(0);
  const [totalHaircuts, setTotalHaircuts] = useState(0);
  const [barberEarnings, setBarberEarnings] = useState({});

  useEffect(() => {
    const storedTransactions = JSON.parse(localStorage.getItem("transactions")) || [];

    let revenue = 0;
    let haircutsCount = storedTransactions.length;
    let barberEarningsMap = {};

    storedTransactions.forEach((transaction) => {
      revenue += transaction.totalPrice;

      if (!barberEarningsMap[transaction.barber]) {
        barberEarningsMap[transaction.barber] = 0;
      }
      barberEarningsMap[transaction.barber] += transaction.barberShare;
    });

    setTotalRevenue(revenue);
    setTotalHaircuts(haircutsCount);
    setBarberEarnings(barberEarningsMap);
  }, []);

  return (
    <Container>
      <Typography variant="h4" sx={{ mt: 4 }}>لوحة التحكم</Typography>

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, textAlign: "center", backgroundColor: "#e3f2fd" }}>
            <Typography variant="h6">إجمالي الإيرادات</Typography>
            <Typography variant="h4">{totalRevenue} جنيه</Typography>
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, textAlign: "center", backgroundColor: "#c8e6c9" }}>
            <Typography variant="h6">عدد الحلاقات</Typography>
            <Typography variant="h4">{totalHaircuts}</Typography>
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, textAlign: "center", backgroundColor: "#fff3e0" }}>
            <Typography variant="h6">أرباح الحلاقين</Typography>
            {Object.keys(barberEarnings).length > 0 ? (
              Object.keys(barberEarnings).map((barber, index) => (
                <Typography key={index}>{barber}: {barberEarnings[barber]} جنيه</Typography>
              ))
            ) : (
              <Typography>لا توجد بيانات</Typography>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
