import React, { useState, useEffect } from "react";
import { Container, Typography, FormControl, InputLabel, Select, MenuItem, Button, List, ListItem, ListItemText } from "@mui/material";

const Cashier = () => {
  const [services, setServices] = useState([]);
  const [barbers, setBarbers] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [selectedBarber, setSelectedBarber] = useState("");
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // تحميل البيانات من localStorage عند فتح الصفحة
    setServices(JSON.parse(localStorage.getItem("services")) || []);
    setBarbers(JSON.parse(localStorage.getItem("barbers")) || []);
    setTransactions(JSON.parse(localStorage.getItem("transactions")) || []);
  }, []);

  useEffect(() => {
    // حفظ العمليات بعد كل تحديث
    localStorage.setItem("transactions", JSON.stringify(transactions));
  }, [transactions]);

  // دالة لحجز خدمة
  const handleCheckout = () => {
    if (!selectedService || !selectedBarber) return;

    const service = services.find(s => s.name === selectedService);
    if (!service) return;

    const totalPrice = service.price;
    const barberShare = totalPrice / 2;

    const newTransaction = {
      service: selectedService,
      barber: selectedBarber,
      totalPrice,
      barberShare
    };

    setTransactions([...transactions, newTransaction]);
    setSelectedService("");
    setSelectedBarber("");
  };

  return (
    <Container>
      <Typography variant="h4" sx={{ mt: 4 }}>واجهة الكاشير</Typography>

      {/* اختيار الخدمة */}
      <FormControl fullWidth sx={{ mt: 2 }}>
        <InputLabel>اختر الخدمة</InputLabel>
        <Select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
          {services.map((service, index) => (
            <MenuItem key={index} value={service.name}>{service.name} - {service.price} جنيه</MenuItem>
          ))}
        </Select>
      </FormControl>

      {/* اختيار الحلاق */}
      <FormControl fullWidth sx={{ mt: 2 }}>
        <InputLabel>اختر الحلاق</InputLabel>
        <Select value={selectedBarber} onChange={(e) => setSelectedBarber(e.target.value)}>
          {barbers.map((barber, index) => (
            <MenuItem key={index} value={barber.name}>{barber.name}</MenuItem>
          ))}
        </Select>
      </FormControl>

      {/* زر الحجز */}
      <Button variant="contained" color="primary" sx={{ mt: 2 }} onClick={handleCheckout}>
        إتمام الدفع
      </Button>

      {/* عرض قائمة العمليات */}
      <Typography variant="h5" sx={{ mt: 4 }}>السجلات الأخيرة</Typography>
      <List>
        {transactions.map((txn, index) => (
          <ListItem key={index}>
            <ListItemText primary={`${txn.service} - ${txn.barber}`} secondary={`الإجمالي: ${txn.totalPrice} جنيه | نصيب الحلاق: ${txn.barberShare} جنيه`} />
          </ListItem>
        ))}
      </List>
    </Container>

  );
};

export default Cashier;
